import net from "net";
export function findAvailablePort() {
    return new Promise((resolve, reject) => {
        const server = net.createServer();
        server.on("error", reject);
        server.listen(0, () => {
            const address = server.address();
            server.on("close", resolve.bind(null, address.port));
            server.close();
        });
    });
}
